import { Component } from 'react'

class Tambah extends Component {
  constructor(props) {
    super(props);
    this.state = {
        room: [],
        name: '',
        major: '',
        room_id: '',
    };
  }

  componentDidMount() {
    fetch('http://127.0.0.1:8000/api/room')
      .then(response => response.json())
      .then(data => this.setState({ room: data.data }));
  }

  handleChange = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };

  handleSubmit = async (event) => {
    event.preventDefault();
    const { name, major, room_id } = this.state;

    console.log(name+""+major+""+room_id)
    fetch('http://127.0.0.1:8000/api/send-guru', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        name,
        major,
        room_id,
      }),
    })
      .then(response => response.json())
      .then(data => {
        if (data.success) { // Assume your API returns a success flag
          alert('Data berhasil ditambahkan!');
          this.setState({ name: '', major: '', room_id: '' });
        } else {
          alert('Gagal menambahkan data');
        }
        console.log(object)
      })
      .catch(error => console.error('Error:', error));
  };

  render() {
    return (
      <div className='container'>
        <form onSubmit={this.handleSubmit}>
          <table className="table mt-4">
            <tbody>
              <tr>
                <td width="200">Nama</td>
                <td width="1">:</td>
                <td>
                  <input
                    type="text"
                    value={this.state.name}
                    onChange={this.handleChange}
                    name="name"
                    className="form-control"
                    required
                  />
                </td>
              </tr>
              <tr>
                <td>Jurusan</td>
                <td>:</td>
                <td>
                  <input
                    type="text"
                    value={this.state.major}
                    onChange={this.handleChange}
                    name="major"
                    className="form-control"
                    required
                  />
                </td>
              </tr>
              <tr>
                <td>Kelas</td>
                <td>:</td>
                <td>
                  <select
                    name="room_id"
                    value={this.state.room_id}
                    onChange={this.handleChange}
                    className="form-control"
                    required
                  >
                    <option value="" disabled>Pilih Kelas</option>
                    {this.state.room.map((item) => (
                      <option key={item.id} value={item.id}>
                        {item.name}
                      </option>
                    ))}
                  </select>
                </td>
              </tr>
            </tbody>
          </table>
          <div className="d-flex justify-content-end mt-4">
            <button type="submit" className="btn btn-success">Simpan</button>
          </div>
        </form>
      </div>
    );
  }
}

export default Tambah;
