import { Component } from 'react'

class Tambah extends Component {
  constructor(props) {
    super(props);
    this.state = {
        room: [],
        name: '',
        major: '',
        room_id: '',
    };
  }

  componentDidMount() {
      fetch('http://127.0.0.1:8000/api/room')
      .then(response => response.json())
      .then(data => this.setState({room: data.data}));
  }

  handleChange = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };

  handleSubmit = async (event) => {
    event.preventDefault();
    const { name, major, room_id } = this.state;
    
    fetch('http://127.0.0.1:8000/api/send-guru', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          name,
          major,
          room_id
        }),
      })
      .then(response => response.json())
      .then(data => this.setState({ name: '', major: '', room_id: 0 }));
  };


  render() {
    return (
      <div className='container'>
        <table class="table mt-4">
          <tr>
            <td width="200">Nama</td>
            <td width="1">:</td>
            <td><input type="text" value={this.state.name} onChange={this.handleChange} name="nama" class="form-control"/></td>
          </tr>
          <tr>
            <td>Mata Pelajaran</td>
            <td>:</td>
            <td><input type="text" value={this.state.major} onChange={this.handleChange}  name="mata_pelajaran"  class="form-control"/></td>
          </tr>
          <tr>
            <td>Kelas</td>
            <td>:</td>
            <td>
              <select name="kelas" class="form-control">
                <option className="" selected="selected" disabled>
                  Ruang
                </option>
                {this.state.room.map((item) => (
                  <option key={item.id} value={item.id}>
                    {item.name}
                  </option>
                ))}
              </select>
            </td>
          </tr>
        </table>
        <div className="d-flex justify-content-end  mt-4">
          <button type="submit" class="btn btn-success ">Simpan</button>
        </div>
      </div>
    )
  }
}

export default Tambah;
