import { Component } from 'react'

class Tambah extends Component {
  constructor(props) {
    super(props);
    this.state = {
        room: [],
        name: ''
    };
  }

  componentDidMount() {
      fetch('http://127.0.0.1:8000/api/room')
      .then(response => response.json())
      .then(data => this.setState({room: data.data}));
  }

  handleChange = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };

  handleSubmit = async (event) => {
    event.preventDefault();
    const { title, body } = this.state;
    try {
      const response = await fetch('http://your-laravel-api-endpoint/api/posts', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          title,
          body,
        }),
      });
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const jsonData = await response.json();
      console.log(jsonData);
      alert('Data berhasil ditambahkan!');
      this.setState({ title: '', body: '' });
    } catch (error) {
      console.error('Error:', error);
    }
  };


  render() {
    return (
      <div className='container'>
        <table class="table mt-4">
          <tr>
            <td width="200">Nama</td>
            <td width="1">:</td>
            <td><input type="text" name="nama" class="form-control"/></td>
          </tr>
          <tr>
            <td>Mata Pelajaran</td>
            <td>:</td>
            <td><input type="text" name="mata_pelajaran"  class="form-control"/></td>
          </tr>
          <tr>
            <td>Kelas</td>
            <td>:</td>
            <td>
              <select name="kelas" class="form-control">
                <option className="" selected="selected" disabled>
                        Ruang
                      </option>
                      {this.state.room.map((item) => (
                        <option key={item.id} value={item.id}>
                          {item.name}
                        </option>
                      ))}
              </select>
            </td>
          </tr>
        </table>
        <div className="d-flex justify-content-end  mt-4">
          <button type="submit" class="btn btn-success ">Simpan</button>
        </div>
      </div>
    )
  }
}

export default Tambah;
