import logo from './logo.svg';
import './App.css';
import { Component } from 'react';
import Data from './Data';

function Halo(props) {
  return(
    <div>
      <h1>Halo, apakabar {props.name}?</h1>
    </div>
  )
}

class Menyapa extends Component{
  render = (props) =>{
    return(
      <div>
        Halo, saya menyapa {this.props.name}
      </div>
    )
  }
}

function Hitung(props) {
  return(
    <div>
      Hasil dari {props.a1} + {props.a2} = {parseInt(props.a1) + parseInt(props.a2)}
    </div>
  )
}


class Timer extends Component{

  constructor(props){
    super(props)

    this.state = {
      waktu : 0
    }
  }

  mulai = () => {
    let self = this;
    this.interval = setInterval(function() {
      self.setState({
        waktu : parseInt(self.state.waktu) + 1
      })
    }, 1000)
  }

  pause = () =>{
    clearInterval(this.interval)
  }

  stop = () => {
    clearInterval(this.interval)

    this.setState({
      waktu : 0
    });
  }

  render = () => {
    return(
      <div>
        <h1>{this.state.waktu}</h1>
        <button onClick={this.mulai}>Mulai</button>
        <button onClick={this.pause}>Pause</button>
        <button onClick={this.stop}>Stop</button>
      </div>
    )
  }
}

function App() {
  return (
    <div className="App">
      <Data />
    </div>
  );
}

export default App;
